DROP DATABASE IF EXISTS final_project;
CREATE DATABASE final_project;
USE final_project;

DROP USER IF EXISTS 'api'@'localhost';
CREATE USER 'api'@'localhost' IDENTIFIED BY 'finalproject';
GRANT ALL PRIVILEGES ON final_project.* TO 'api'@'localhost';

CREATE TABLE Shops(
    shop_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    owner VARCHAR(255) NOT NULL,
    address VARCHAR(255)
);

CREATE TABLE Items(
    item_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    category ENUM ('Adventuring Gear','Armor','Tools','Wondrous Items','Weapons','Poisons','Potions & Oils','Miscellaneous'),
    rarity ENUM ('Mundane','Common','Uncommon','Rare','Very Rare','Legendary'),
    description TEXT,
    shop_id INT REFERENCES Shops(shop_id),
    price INT,
    stocked INT
);

CREATE UNIQUE INDEX magic_items ON Items(name);

CREATE TABLE Adventurers(
    adventurer_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    address VARCHAR(255)
);

CREATE TABLE Logins (
    login_id INT PRIMARY KEY AUTO_INCREMENT,
    adventurer_id INT NULL,
    login_time DATETIME NOT NULL DEFAULT NOW()
);

CREATE TABLE Orders(
    order_id INT PRIMARY KEY,
    adventurer_id INT REFERENCES Adventurers(adventurer_id),
    total_price INT,
    purchase_time DATETIME 
);

CREATE TABLE OrderItems(
    order_id INT REFERENCES Orders(order_id),
    item_id INT REFERENCES Items(item_id),
    quantity INT,
    PRIMARY KEY(order_id, item_id)
);

CREATE INDEX items_ordered ON OrderItems(order_id);

ALTER TABLE Logins
ADD CONSTRAINT fk_login_adventurer
FOREIGN KEY (adventurer_id) REFERENCES Adventurers(adventurer_id)
ON DELETE SET NULL;


CREATE TABLE 90DaySalesSummary (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255),
    category ENUM ('Adventuring Gear','Armor','Tools','Wondrous Items','Weapons','Poisons','Potions & Oils','Miscellaneous'),
    rarity ENUM('Mundane','Common','Uncommon','Rare','Very Rare','Legendary'),
    total_sold INT,
    total_revenue INT,
    orders_containing_item INT,
    last_updated DATETIME
);






DELIMITER $$

CREATE PROCEDURE SetOrderTotal(IN o_id INT)
BEGIN
    UPDATE Orders
    SET total_price = (
        SELECT COALESCE(SUM(oi.quantity * i.price), 0)
        FROM OrderItems oi
        JOIN Items i ON oi.item_id = i.item_id
        WHERE oi.order_id = o_id
    )
    WHERE order_id = o_id;
END $$

DELIMITER ;

DELIMITER $$

CREATE PROCEDURE SetAllOrderTotals()
BEGIN
    UPDATE Orders o
    SET total_price = (
        SELECT COALESCE(SUM(oi.quantity * i.price), 0)
        FROM OrderItems oi
        JOIN Items i ON oi.item_id = i.item_id
        WHERE oi.order_id = o.order_id
    );
END $$

DELIMITER ;

DELIMITER $$

CREATE PROCEDURE SearchItems(
    IN p_name VARCHAR(255),
    IN p_category VARCHAR(255),
    IN p_rarity VARCHAR(50),
    IN p_min_price INT,
    IN p_max_price INT
)
BEGIN
    SELECT item_id, name, category, rarity, price, stocked, shop_id, description
    FROM Items
    WHERE
        (p_name IS NULL OR name LIKE CONCAT('%', p_name, '%'))
        AND (p_category IS NULL OR category LIKE CONCAT('%', p_category, '%'))
        AND (p_rarity IS NULL OR rarity = p_rarity)
        AND (p_min_price IS NULL OR price >= p_min_price)
        AND (p_max_price IS NULL OR price <= p_max_price)
    ORDER BY name, rarity;
END $$

DELIMITER ;

DELIMITER $$

CREATE PROCEDURE TopSellingItems(IN p_days INT)
BEGIN
    SELECT
        i.item_id,
        i.name AS item_name,
        i.category,
        i.rarity,
        SUM(oi.quantity) AS total_sold,
        SUM(oi.quantity * i.price) AS total_revenue
    FROM OrderItems oi
    JOIN Items i ON oi.item_id = i.item_id
    JOIN Orders o ON oi.order_id = o.order_id
    WHERE (p_days = 0 OR o.purchase_time >= NOW() - INTERVAL p_days DAY)
    GROUP BY i.item_id, i.name, i.category, i.rarity
    ORDER BY total_sold DESC, total_revenue DESC;
END $$

DELIMITER ;

DELIMITER $$

CREATE PROCEDURE RefreshSummary()
BEGIN
    CALL SetAllOrderTotals();

    TRUNCATE TABLE 90DaySalesSummary;

    SELECT
        i.item_id,
        i.name,
        i.category,
        i.rarity,
        COALESCE(SUM(CASE WHEN o.purchase_time >= NOW() - INTERVAL 90 DAY THEN oi.quantity ELSE 0 END), 0) AS total_sold,
        COALESCE(SUM(CASE WHEN o.purchase_time >= NOW() - INTERVAL 90 DAY THEN oi.quantity * i.price ELSE 0 END), 0) AS total_revenue,
        COALESCE(SUM(CASE WHEN o.purchase_time >= NOW() - INTERVAL 90 DAY THEN 1 ELSE 0 END), 0) AS orders_containing_item,
        NOW() AS last_updated
    FROM Items i
    LEFT JOIN OrderItems oi ON i.item_id = oi.item_id
    LEFT JOIN Orders o ON oi.order_id = o.order_id
    GROUP BY i.item_id, i.name, i.category, i.rarity
    ORDER BY total_sold DESC, total_revenue DESC;

    UPDATE 90DaySalesSummary SET last_updated = NOW();
END $$

DELIMITER ;

CALL RefreshSummary();